package ch_3_1;

public class ch_3_2_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//증감연산자 ++, --
		
		int a = 0;
		
		int b = ++a; // a = a + 1(1씩 늘어남)
		
		int c = 10;
		
		int d = --c;
		
		System.out.println("a: " + a);
		System.out.println("b: " + b);
		System.out.println("c: " + c);
		System.out.println("d: " + d);

	}

}
